import React from 'react';
import { Link } from 'react-router-dom';
import './CSS/Search.css'
import {uid} from 'react-uid';

class Search extends React.Component{
    render(){
        return(
            <div>
                <SearchBar/>
                <TableResults/>
                <SearchInstructions/>
            </div>
        )

    }
}

class TableResults extends React.Component{

    state = {
        lists:[
            {listName : "Beverages", 
            listDescript : "Some of my favorite homemade beverages",
            username : "John12"},

            {listName : "Dinner", 
            listDescript : "Food for dinner",
            username : "Nathan165"},

            {listName : "Candy", 
            listDescript : "For when I have a sweet tooth",
            username : "Juliet06e"},
        ]
    }


    Row(row){
        return(
            <tr key={uid(row)}>
                <td>
                    <Link to ={'./IndividualList'}>
                        <p>{row.listName}</p>
                    </Link>
                </td>
                <td>
                    <p>{row.listDescript}</p>
                </td>
                <td>
                    <Link to ={'./IndividualList'}>
                    <p>{row.username}</p>
                    </Link>
                </td>
            </tr>
        )
    }

    render(){
        return(
            <table id = "tableDiv">
                <tbody>
                <tr>
                    <th>List Name</th>
                    <th>List Description</th>
                    <th>Username</th>
                </tr>
                {this.state.lists.map(row => {return this.Row(row)})}
                </tbody>
            </table>
        )
    }
}


class SearchBar extends React.Component{
    state = {
        searchBy: 'food'
    }

    searchChanged = (event) => {
        const target = event.target
        const value = target.value
        const name = target.name

        this.setState({
            [name]: value
        })
    }

    render(){
        return(
            <form id="searchBarForm">
                <select name="searchBy" 
                        id="dropDownSearch"
                        onChange={this.searchChanged}>
                    <option value="list">List name</option>
                    <option value="food">Food name</option>
                </select>
                <input type="text" id="searchBarText"></input>
                <input type="submit" id="searchBarButton" value ="Search"></input>

            </form>

        )
    }
}

class SearchInstructions extends React.Component{
    render(){
        return(
            <div id="searchInstructions">
                <p>In the drop down menu, select whether to search by a food item or a list.</p>
                <p>If you search by a food item, you will be given lists that have that food item ranked at the top.</p>
                <p>If you search by a list name, lists that share as many of the same items will be found.</p>
            </div>
        )
    }
}


export default Search;
