const domain = "http://192.168.0.146:5000/"

export const signupFetch = (signupInfo, app) => {
    // Create our request constructor with all the parameters we need
    const request = new Request(domain + `/users/signup`, {
        method: "post",
        body: JSON.stringify(signupInfo.state),
        headers: {
            Accept: "application/json, text/plain, */*",
            "Content-Type": "application/json"
        }
    });

    // Send the request with fetch()
    fetch(request)
        .then(res => {
            if (res.status === 200) {
                return res.json();
            }
        })
        .catch(error => {
            console.log(error);
        });
};


export const login = (loginInfo, app) => {
    // Create our request constructor with all the parameters we need
    const request = new Request(domain + `/users/login`, {
        method: "post",
        body: {
            "username": loginInfo.usernameInput,
            "password": loginInfo.passwordInput
        },
        headers: {
            Accept: "application/json, text/plain, */*",
            "Content-Type": "application/json"
        }
    });

    // Send the request with fetch()
    fetch(request)
        .then(res => {
            if (res.status === 200) {
                return res.json();
            }
        })
        .then(json => {
            if (json.currentUserId !== undefined) {
                app.setState({ userid: json.currentUserId });
            }
        })
        .catch(error => {
            console.log(error);
        });
};
