import React from 'react';
import './IndividualFriendsUserSetting.css'
import {uid} from "react-uid";
import {Link} from "react-router-dom";
class AllUsers extends React.Component{
    state = {
        AllUsers: ["Grace", "Cecily", "Cecelia", "Abby", "Bob", "Cathrine", "George", 'Mia']
    }

    deleteUser = (username) => {
        const filterUsers= this.state.AllUsers.filter(
            (check) =>{
                return check !== username
            }
        )
        this.setState(
            {
                AllUsers: filterUsers
            }
        )
    }

    AllUsersRow(username){
        return(
            <tr key={uid(username)}>
                <td>
                    <Link to={'/Profile'}>
                        <button type="button" className='UsernameButton'> {username}  </button>
                    </Link>
                </td>
                <td>
                    <button type="button" className='DeleteFriend'
                            onClick = {
                                () => this.deleteUser(username)
                            }
                    > delete </button>
                </td>
            </tr>
        )
    }


    AllUsersTable(){
        return (
            <table id='AllUsersTable'>
                <tbody>
                <tr>
                    <th>username</th>
                    <th>Delete</th>
                </tr>
                {this.state.AllUsers.map(username => {return this.AllUsersRow(username)})}
                </tbody>
            </table>
        )
    }

    AllUsersPanel1(){
        return(
            <div id='AllUsersPanel1'>
                <h1> All the users:</h1>
                {this.AllUsersTable()}
            </div>
        )
    }

    AllUsersPanel2(){
        return(
            <div id="AllUsersPanel2">
                <p>
                    <strong>Dear Admin</strong>
                    <br />
                    Deleting a user is not good.
                    <br />
                    Think about the decision you make carefully before doing it.
                    <br />
                    We have warned you!
                </p>
            </div>
        )
    }

    render() {
        return (
            <div>
                {/*<Link to={'/Main'}>*/}
                {/*    <div><button className='back'>Back</button></div>*/}
                {/*</Link>*/}
                <hr/>
                {this.AllUsersPanel1()}
                {this.AllUsersPanel2()}
            </div>
        );
    }
}
export default AllUsers
