import React from 'react';
import { Link } from 'react-router-dom';
import './CSS/Main.css'
import Chart from './Chart'

class Main extends React.Component{
    state = {
     seen: false
     };

     togglePop = () => {
        this.setState({
         seen: !this.state.seen
        });
       };

    render(){
        return(
            <div>
                <TopHeader/>
                <MyLists/>
                <Popularity/>
            <div className="btn" onClick={this.togglePop}>
                <button>Not enough lists? Create a new one!</button>
            </div>
            {this.state.seen ? <NewListPopUp toggle={this.togglePop} /> : null}
            </div>
        )
    }
}

class TopHeader extends React.Component{
    render(){
        return(
            <div >
                Main Page
                <Link to={'/Search'}>
                    <button className="topButtons">Search for Similar Lists</button>
                </Link>
                <Link to={'/Friends'}>
                    <button className="topButtons">Friends</button>
                </Link>
                <Link to={'/Friends'}>
                    <button className="topButtons">Profile</button>
                </Link>
                <hr/>
            </div>
        )
    }
}

class MyLists extends React.Component{
    state = {
        lists: [
            {id: 1, name: 'Burgers'},
            {id: 2, name: 'Beverages'},
            {id: 3, name: 'Dishes'},
            {id: 4, name: 'Ingredients'}
        ]
    }
    render(){
        return(
            <div id="foodListSpan">
                <h3>My Lists</h3>
                <div id="grid-container">
                    {this.state.lists.map((list) =>{
                        return(
                            <Link to={'/IndividualList'}>
                                <button className="listButtons">{list.name}</button>
                            </Link>
                        )
                    })} 
                </div>
            </div>
        )
    }
}

class Popularity extends React.Component{
    state = {
        sortBy: 'dish'
    }

    sortChanged = (event) => {
        const target = event.target
        const value = target.value
        const name = target.name

        this.setState({
            [name]: value
        })
    }

    render(){
        return(
            <div id="popularitySpan">
                <label for="popularity" class="prompt">Rank popularity by:</label>
                <select name="sortBy" 
                        id="popularity"
                        onChange={this.sortChanged}>
                    <option value="dish">Dish</option>
                    <option value="beverage">Beverage</option>
                    <option value="ingredient">Ingredient</option>
                </select>
                <h4>Winners at the moment:</h4>
                <Chart id="chart" 
                       option={this.state.sortBy}>
                </Chart>
            </div>
        )
    }
}

class NewListPopUp extends React.Component{
    state = {
        listName: "",
        category: "",
        permission: "",
        descript: ""
    }

    handleTextChange = (event) => {
        const target = event.target
        const value = target.value
        const name = target.name

        this.setState({
            [name]: value
        })
    }

    handleCloseClick = () => {
        this.props.toggle();
    };

    handleSubmitClick = () => {
        /*
            Make post request to insert new list information to database.
            Pass in values in state.
        */
    }

    render(){
        return(
            <div className="popup">     
                <form className="form">
                    <span className="closeX" onClick={this.handleCloseClick}>&times;</span>
                    <br></br>
                    <label className="popupLabel" for="listName"><b>List Name:</b></label>
                    <input className="popupTextbox" type="text" name="listName" onChange={this.handleTextChange}></input><br/>
                    <label className="popupLabel" for="category"><b>Category:</b></label>
                    <input className="popupTextbox"  type="text" name="category" onChange={this.handleTextChange}></input><br/>
                    <label className="popupLabel" for="permission"><b>Permission:</b></label>
                    <input className="popupTextbox"  type="text" name="permission"onChange={this.handleTextChange}></input><br/>
                    <label className="popupLabel" for="description"><b>List description:</b></label>
                    <input className="popupTextbox"  type="text" name="descript" onChange={this.handleTextChange}></input><br></br><br></br>
                    <input className="popupButton" 
                           type="submit" 
                           value="Submit"
                           onClick={this.handleSubmitClick}></input>
                </form>
            </div>
        )
    }
}


export default Main;
