import ENV from './../config.js'
const API_HOST = ENV.api_host

// Get all list ids and names of currently logged in user
export const get_all_lists= (app) => {
    const url = `${API_HOST}/get_all_lists`;
    fetch(url)
        .then(res => {
            if (res.status === 200) {
                return res.json()
            }
        })
        .then(json =>{
            app.setState({lists: json})
        })
        .catch(error => {
            console.log(error);
        });
};

// Post a new list of the current user to the DB
export const create_list = (listName, category, listDescription, permissions) => {
    const request = new Request(`${API_HOST}/create_list`, {
        method: "post",
        body: {
            "listName": listName,
            "category": category,
            "listDescription": listDescription,
            "permissions": permissions
        },
        headers: {
            Accept: "application/json, text/plain, */*",
            "Content-Type": "application/json"
        }
    });

    // Send the request with fetch()
    fetch(request)
        .then(res => {
            if (res.status === 200) {
                return res.json();
            }
        })
        .then(json => {
            if (json.currentUser !== undefined) {
                // app.setState({ currentUser: json.currentUser });
                // loginComp.setState({ loggedIn: 1 })
            }
        })
        .catch(error => {
            console.log(error);
        });
}
