import ENV from './../config.js'
const API_HOST = ENV.api_host

// get profile information by user id
export const get_user_profile_info = (app, userid) => {
    const url = `${API_HOST}/get_user_info/` +  userid;
    fetch(url)
        .then(res => {
            if (res.status === 200) {
                return res.json()
            }
        })
        .then(json =>{
            app.setState({ json })
        })
        .catch(error => {
            console.log(error);
        });
}
