import React from 'react';
import { Route, Switch, BrowserRouter} from 'react-router-dom'
import './App.css';
import Home from './Home'
import Login from './Login'
import Signup from './Signup'
import IndividualList from "./IndividualList"
import Friends from './Friends'
import AllUsers from "./AllUsers";
import Setting from "./Setting";
import Main from "./Main"
import MainAdmin from "./MainAdmin"
import Search from "./Search"
import Profile from "./profile"

import { checkSession } from "./actions/user";

class App extends React.Component{

  componentDidMount() {
    checkSession(this); // sees if a user is logged in
  }

  // global state passed down includes the current logged in user.
  state = {
    currentUser: null
  }

  render(){
    const { currentUser } = this.state;
    return (
      <BrowserRouter>
        <Switch>
            <Route 
                    exact path='/'
                    render={() => (<Home/>)} />
            <Route
                    exact path={["/", "/login", "/main"] /* any of these URLs are accepted. */ }
                    render={ props => (
                        <div className="app">
                            { /* Different componenets rendered depending on if someone is logged in. */}
                            {!currentUser ? <Login {...props} app={this} /> : <Dashboard {...props} app={this} />}
                        </div>                   // ... spread operator - provides all of the props in the props object

                    )}
            />
            <Route 
                    exact path='/signup'
                    render = {() => (<Signup/>) }/>
            <Route
                exact path='/IndividualList'
                render = {() => (<IndividualList/>) }/>
            <Route
                exact path='/Friends'
                render = {() => (<Friends/>) }/>
            <Route
                exact path='/AllUsers'
                render = {() => (<AllUsers/>) }/>
            <Route
                exact path='/Setting'
                render = {() => (<Setting/>) }/>
            <Route
                exact path='/Main'
                render = {() => (<Main/>) }/>
            <Route
                exact path='/MainAdmin'
                render = {() => (<MainAdmin/>) }/>
            <Route
                exact path='/Search'
                render = {() => (<Search/>) }/>
            <Route
                exact path='/Profile'
                render = {() => (<Profile />) }/>          
        </Switch>
      </BrowserRouter>
    )
  }
}

export default App;
