import React from 'react';
import {Bar} from 'react-chartjs-2';


class Chart extends React.Component {
  constructor(props){  
    super(props);
    this.state = {
    dish: {
      labels: ['Burgers', 'Pies', 'Pizza'],
      datasets: [
      {
        label: 'Top 3 Popular Dishes',
        backgroundColor: 'rgba(75,192,192,1)',
        borderColor: 'rgba(0,0,0,1)',
        borderWidth: 2,
        data: [65, 59, 80]
      }
      ]
    },
    beverage: {
      labels: ['Sprite', 'Tea', 'Coffee'],
      datasets: [
      {
        label: 'Top 3 Popular Beverages',
        backgroundColor: 'rgba(75,192,192,1)',
        borderColor: 'rgba(0,0,0,1)',
        borderWidth: 2,
        data: [60, 27, 14]
      }
      ]
    },
    ingredient: {
      labels: ['Garlic', 'Onion', 'Pepper'],
      datasets: [
      {
        label: 'Top 3 Popular Ingredients',
        backgroundColor: 'rgba(75,192,192,1)',
        borderColor: 'rgba(0,0,0,1)',
        borderWidth: 2,
        data: [60, 27, 14]
      }
      ]
    }
    };
  }

  render() {

    const { option } = this.props;

    return (
      <div>
        <Bar
          data={this.state[option]}
          options={{
            title:{
              display:true,
              text:'',
              fontSize:20
            },
            legend:{
              display:false,
              position:'right'
            }
          }}
        />
      </div>
    );
  }
}
export default Chart;
