import React from 'react';
import { Link } from 'react-router-dom';
import './CSS/Main.css'
import Chart from './Chart'
class Main extends React.Component{

    state = {
        lists: [
            {id: 1, name: 'Burgers'},
            {id: 2, name: 'Beverages'},
            {id: 3, name: 'Dishes'},
            {id: 4, name: 'Ingredients'}
        ],
        sortBy: 'dish'
    }

    sortChanged = (event) => {
        const target = event.target
        const value = target.value
        const name = target.name

        this.setState({
            [name]: value
        })
    }

    TopButtons(){
        return (
            <div >
                Main Page
                <Link to={'/IndividualList'}>
                    <button className="topButtons">Search for Similar Lists</button>
                </Link>
                <Link to={'/Friends'}>
                    <button>Friends</button>
                </Link>
                <Link to={'/Friends'}>
                    <button>Profile</button>
                </Link>
                <hr/>
            </div>
        )
    }

    render(){
        return(
            <div>
                TopButtons()

                <div id="foodListSpan">
                    <h3>My Lists</h3>
                    <div id="grid-container">
                    {this.state.lists.map((list) =>{
                        return(
                            <Link to={'/IndividualList'}>
                                <button className="listButtons">{list.name}</button>
                            </Link>
                        )
                    })} 
                    </div>
                </div>

                <div id="popularitySpan">
                    <label for="popularity" class="prompt">Sort By:</label>
                    <select name="sortBy" 
                            id="popularity"
                            onChange={this.sortChanged}>
                        <option value="dish">Dish</option>
                        <option value="beverage">Beverage</option>
                        <option value="ingredient">Ingredient</option>
                    </select>
                    <h4>Winners at the moment:</h4>
                    <Chart id="chart" 
                           option={this.state.sortBy}>
                     </Chart>
                    <button id="notEnough">Not Enough Lists? Create a new one!</button>
                </div>
            </div>
        )
    }
}

export default Main;
