import React from 'react';
import './IndividualFriendsUserSetting.css'
import {uid} from "react-uid";
import {Link} from "react-router-dom";
import {send_friend_request} from "./actions/friend";

class Friends extends React.Component{
    state = {
        ExistingFriendsList: ["Grace", "Cecily", "Cecelia"],
        PotentialFriendsList: ["Abby", "Bob", "Cathrine", "George"],
        usernameInput: ""
    }

    handleChange(event){
        this.setState({[event.target.name]: event.target.value});
    }

    handleSubmit(event){
        alert("Sending the request");
        send_friend_request(this.state.usernameInput)
        alert('Sended! Be patient!!!')
    }

    deleteFriend = (username) => {
        const filterFriends= this.state.ExistingFriendsList.filter(
            (check) =>{
                return check !== username
            }
        )
        this.setState(
            {
                ExistingFriendsList: filterFriends
            }
        )
    }

    ExistingFriendsRow(username){
        return(
            <tr key={uid(username)}>
                <td>
                    <Link to={'/Profile'}>
                        <button type="button" className='UsernameButton'> {username} </button>
                    </Link>

                </td>
                <td>
                    <button type="button" className='DeleteFriend'
                            onClick = {
                                () => this.deleteFriend(username)
                            }
                    > delete
                    </button>
                </td>
            </tr>
        )
    }



    ExistingFriends() {
        return (
            <table id='ExistingFriends'>
                <tbody>
                <tr>
                    <th>username</th>
                    <th>Delete</th>
                </tr>
                {this.state.ExistingFriendsList.map(username => {return this.ExistingFriendsRow(username)})}
                </tbody>
            </table>
        )
    }
    Panel1(){
        return(
            <div id="FriendsPanel1">
                <h1>My Friends List</h1>
                {this.ExistingFriends()}
            </div>
        )
    }

    rejectFriend = (username) => {
        const filterFriends= this.state.PotentialFriendsList.filter(
            (check) =>{
                return check !== username
            }
        )
        this.setState(
            {
                PotentialFriendsList: filterFriends
            }
        )
    }

    acceptFriend = (username) => {
        const filterFriends= this.state.PotentialFriendsList.filter(
            (check) =>{
                return check !== username
            }
        )
        let Friends = this.state.ExistingFriendsList

        Friends.push(username)
        this.setState(
            {
                PotentialFriendsList: filterFriends,
                ExistingFriendsList: Friends
            }
        )
    }

    PotentialFriendsRow(username){
        return(
            <tr key={uid(username)}>
                <td>
                    <Link to={'/Profile'}>
                        <button type="button" className='UsernameButton'> {username}  </button>
                    </Link>
                </td>
                <td>
                    <button type="button" className='AcceptFriend'
                            onClick={
                                () => this.acceptFriend(username)
                            }
                    > Accept! </button>
                </td>
                <td>
                    <button type="button" className='RejectFriend'
                            onClick={
                                () => this.rejectFriend(username)
                            }
                    > NO! </button>
                </td>
            </tr>
        )
    }


    PotentialFriends(){
        return (
            <table id="PotentialFriends">
                <tbody>
                <tr>
                    <th>username</th>
                    <th>Accept</th>
                    <th>Reject</th>
                </tr>
                {this.state.PotentialFriendsList.map(username => {return this.PotentialFriendsRow(username)})}
                </tbody>
            </table>
        )
    }


    Panel2(){
        return(
            <div id="FriendsPanel2">
                <h1>Who want to be my friends</h1>
                {this.PotentialFriends()}
            </div>
        )
    }



    SendRequestForm(){
        return(
            <form action="#" onSubmit="this.handleSubmit.bind(this); return false;">
                <input type="text" name="usernameInput" className="input"
                       value={this.state.usernameInput} onChange={this.handleChange.bind(this)}
                       placeholder="Username" />
                <br/>
                <input className="submitButton" type="submit" value="Send the Request"/>
            </form>
        )
    }

    Panel3(){
        return(
            <div id="FriendsPanel3">
                <h1>Send a Friends Request</h1>
                {this.SendRequestForm()}

                <br/>
            </div>
        )
    }
    render(){
        return(
            <div>
                {/*<Link to={'/Main'}>*/}
                {/*    <div><button className='back'>Back</button></div>*/}
                {/*</Link>*/}
                <hr/>
                {this.Panel1()}
                {this.Panel2()}
                {this.Panel3()}
            </div>
        )
    }

}
export default Friends;
