import React from 'react';
import './CSS/Profile.css';
import avatar from "./Resources/Profile_avatar_placeholder.png";
import mo1 from "./Resources/mo1.jpg";
import mo2 from "./Resources/mo2.jpg";
import NavigationBar from './NavigationBar'

class Profile extends React.Component{
    render(){
        return(
            <div>
                <NavigationBar app = {this.props.app} history = {this.props.history}/>
                <ProfileHeader />
                <Body />
            </div>
        );
    }
}

class Body extends React.Component{
    render(){
        return(
            <div>
                <img className="avatar" src={avatar}  alt=""></img>
                <ProfileSidebar />
                <TimeLine />
            </div>
        );
    }
}

class ProfileHeader extends React.Component{
    render(){
        return(
            <div className="header">
                <ProfileSearchBar />
            </div>
        );
    }
}

class ProfileSearchBar extends React.Component{
    render(){
        return(
            <form>
                <input type="text" className="searchProfile"
                    placeholder="     Search user profile" />
            </form>
        );
    }
}

class ProfileSidebar extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            //TODO category/data storage
            name: "Max Jin",
            age: "21",
            nationality: "Canadian",
            favouriteFood: "Burger",
            favouriteCuisine: "Viltrumite",
            bio: `I love junk food fast food! Burgers 
            poutines fries but I despise burger priest 
            they served me a cold burger!`,
        };
    }
    render(){
        //TODO for loop, make bio react(css)
        return(
            <div className="profileSidebar">
                <div className="profileInfoList">
                    <ul>
                        <ProfileDetail name="Name" value={this.state.name} />
                        <ProfileDetail name="Age" value={this.state.age} />
                        <ProfileDetail name="Nationality" value={this.state.nationality} />
                        <ProfileDetail name="Favourite Food" value={this.state.favouriteFood} />
                        <ProfileDetail name="Favourite Cuisine" value={this.state.favouriteCuisine} />

                        <li className="detail">
                            <p className="bioName"> About Me: </p> 
                            <p className="bioContent"> {this.state.bio} </p>
                        </li>
                    </ul>
                </div>
                <div className="editProfile">
                    <button className="editButton"> Edit </button>
                </div>
            </div>
        );
    }
}

class ProfileDetail extends React.Component{
    render(){
        return(
            <li className="detail">
                <p className="detailName"> {this.props.name}: </p> 
                <p className="detailContent"> {this.props.value} </p>
            </li>
        );
    }
}

class TimeLine extends React.Component{
    constructor(props){
        super(props);
        this.state={
            moments: [{comment: "Feast time!", picture: mo1}, 
                {comment: "Ingredients for Thanksgiving meal!", picture: mo2}],
        };
    }

    render(){
        return(
            <div className="foodMoments">
                <div className="momentsHeader">
                        <h4> My Food Moments <button className="postButton"> Post </button> </h4>
                </div>

                <Moment caption={this.state.moments[0].comment} picture={this.state.moments[0].picture}/>
                <Moment caption={this.state.moments[1].comment} picture={this.state.moments[1].picture}/>
            </div>
        );
    }
}

class Moment extends React.Component{
    render(){
        return(
            <div className="moment">
                <p className="caption"> {this.props.caption} </p>
                <img className="momentPicture" src={this.props.picture} alt="" />
            </div>
        );
    }
}


export default Profile;
