import React from 'react';
import './IndividualFriendsUserSetting.css'
import {uid} from "react-uid";
class Setting extends React.Component{
    state={
        ListCategories: ["Ingredients", "Dishes", "Categories"],
        IdentityCategories: ["Cook"]
    }

    ListItem(name){
        return(
            <li  key={uid(name)} >
                <button type="button" className='DeleteCategory'> X </button>
                {"      "}
                {name}
            </li>
        )
    }


    ListCategory(List){
        return(
            <ul>
                {List.map(name=>{return this.ListItem(name)})}
            </ul>
        )
    }

    SettingPanel1(){
        return(
            <div id="SettingPanel1">
                <h1> List Category</h1>
                {this.ListCategory(this.state.ListCategories)}
            </div>
        )

    }



    SettingPanel2(){
        return(
            <div id="SettingPanel2">
                <h1> Identity Category</h1>
                {this.ListCategory(this.state.IdentityCategories)}
            </div>
        )
    }

    AddCategoryForm(label){
        return(
            <form>
                <label>
                    <strong> {label}   </strong>
                    <input type="text" name="NewCategory" />
                </label>
                <input type="submit" value="Add" className="AddNewCategoryButton"/>
            </form>
        )
    }

    SettingPanel3(){
        return(
            <div id="SettingPanel3">
                <h1>Add a New Category</h1>
                {this.AddCategoryForm("Add a new list category")}
                <br />
                {this.AddCategoryForm("Add a new identity category")}
            </div>
        )
    }
    render(){
        return(
            <div>
                <div><button className='back'>Back</button></div>
                <hr/>
                {this.SettingPanel1()}
                {this.SettingPanel2()}
                {this.SettingPanel3()}

            </div>
        )
    }
}
export default Setting
