import React from 'react';
import { Link } from 'react-router-dom';
import './CSS/Main.css'
import Chart from './Chart'

class Main extends React.Component{
    state = {
     seen: false
     };

     togglePop = () => {
        this.setState({
         seen: !this.state.seen
        });
       };

    render(){
        return(
            <div>
                <TopHeader/>
                <MyLists/>
                <Popularity/>
                <NewListPopUp/>
            <div className="btn" onClick={this.togglePop}>
                <button>Not enough lists? Create a new one!</button>
            </div>
            {this.state.seen ? <p>Hi</p>: null}
            </div>
        )
    }
}

class TopHeader extends React.Component{
    render(){
        return(
            <div >
                Main Page
                <Link to={'/Search'}>
                    <button className="topButtons">Search for Similar Lists</button>
                </Link>
                <Link to={'/Friends'}>
                    <button className="topButtons">Friends</button>
                </Link>
                <Link to={'/Friends'}>
                    <button className="topButtons">Profile</button>
                </Link>
                <hr/>
            </div>
        )
    }
}

class MyLists extends React.Component{
    state = {
        lists: [
            {id: 1, name: 'Burgers'},
            {id: 2, name: 'Beverages'},
            {id: 3, name: 'Dishes'},
            {id: 4, name: 'Ingredients'}
        ]
    }
    render(){
        return(
            <div id="foodListSpan">
                <h3>My Lists</h3>
                <div id="grid-container">
                    {this.state.lists.map((list) =>{
                        return(
                            <Link to={'/IndividualList'}>
                                <button className="listButtons">{list.name}</button>
                            </Link>
                        )
                    })} 
                </div>
            </div>
        )
    }
}

class Popularity extends React.Component{
    state = {
        sortBy: 'dish'
    }

    sortChanged = (event) => {
        const target = event.target
        const value = target.value
        const name = target.name

        this.setState({
            [name]: value
        })
    }

    render(){
        return(
            <div id="popularitySpan">
                <label for="popularity" class="prompt">Rank popularity by:</label>
                <select name="sortBy" 
                        id="popularity"
                        onChange={this.sortChanged}>
                    <option value="dish">Dish</option>
                    <option value="beverage">Beverage</option>
                    <option value="ingredient">Ingredient</option>
                </select>
                <h4>Winners at the moment:</h4>
                <Chart id="chart" 
                       option={this.state.sortBy}>
                </Chart>
            </div>
        )
    }
}

class NewListPopUp extends React.Component{
    handleClick = () => {
        this.props.toggle();
       };

    render(){
        return(
            <div className="popupWindow" id="form">
            <form className="form">
            <span className="close" onClick={this.handleClick}>&times;    </span>
              <label for="listName"><b>List Name:</b></label>
              <input type="text"></input>
              <label for="category"><b>Category:</b></label>
              <input type="text"></input>
              <label for="permission"><b>Permission:</b></label>
              <input type="text"></input>
              <label for="description"><b>List description::</b></label>
              <input type="text"></input>

              <input type="submit" value="Submit"></input>
              <input type="submit" value="Cancel"></input>
            </form>

            </div>
        )
    }
}


export default Main;
