import React from 'react';
import './CSS/Login.css';
import { Redirect } from 'react-router'
import { signupFetch } from './actions/auth';
import { login } from './actions/session'

class Login extends React.Component {constructor(props){
    super(props);
    this.state = {
        usernameInput: "",
        passwordInput: "",
        loggedIn: 0,
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
}

handleChange(event){
    this.setState({[event.target.name]: event.target.value});
}

handleSubmit(event){
    //TODO authentication check
    if (this.state.usernameInput === "user" 
        && this.state.passwordInput === "user"){
        //TODO page chage
        this.setState({loggedIn: 1});
    }else if (this.state.usernameInput === "admin"
            && this.state.usernameInput === "admin"){
        //TODO page change
        this.setState({loggedIn: 2});
    } else{
        //TODO change notification
        alert("Authentication Failed.");
    }

    event.preventDefault();
}

renderPage(){
    return
}
render(){
    const { app } = this.props
    if(this.state.loggedIn === 1) {
        return <Redirect to="/Main" />
    }else if(this.state.loggedIn === 2){
        return <Redirect to="/MainAdmin" />
    }
    return(
        <div>
            <form onSubmit={() => login(this, app)}>
                <input type="text" name="usernameInput" className="input" 
                value={this.state.usernameInput} onChange={this.handleChange} 
                placeholder="Username" />
                <br/>

                <input type="password" name="passwordInput" className="input" 
                value={this.state.passwordInput} onChange={this.handleChange} 
                placeholder="Password" />
                <br/>

                <input className="submitButton" type="submit" value="Log In"/>
            </form>

            <div className="backLogIn"> <SignUpBar handleClick={() => this.props.handleClick}/> </div>

        </div>
        );
    }
}

class Panel extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            hasAccount: true,
        };
    }

    renderForm(){
        return(
            this.state.hasAccount 
              ? <LoginForm handleClick={() => this.handleClick()} /> 
              : <SignUpForm handleClick={() => this.handleClick()} />
        );
    }

    handleClick(){
        this.setState({hasAccount: !this.state.hasAccount});
    }

    render(){
        return(
        <div className="panel">
            <div className="Header"></div>
            <div className="loginForm">
                    {this.renderForm()}
            </div>
        </div>
        );
    }
}

class LoginForm extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            usernameInput: "",
            passwordInput: "",
            loggedIn: 0,
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event){
        this.setState({[event.target.name]: event.target.value});
    }

    handleSubmit(event){
        //TODO authentication check
        if (this.state.usernameInput === "user" 
            && this.state.passwordInput === "user"){
            //TODO page chage
            this.setState({loggedIn: 1});
        }else if (this.state.usernameInput === "admin"
                && this.state.usernameInput === "admin"){
            //TODO page change
            this.setState({loggedIn: 2});
        } else{
            //TODO change notification
            alert("Authentication Failed.");
        }

        event.preventDefault();
    }

    renderPage(){
        return
    }
    render(){
        const { app } = this.props
        if(this.state.loggedIn === 1) {
            return <Redirect to="/Main" />
        }else if(this.state.loggedIn === 2){
            return <Redirect to="/MainAdmin" />
        }
        return(
            <div>
                <form onSubmit={() => login(this, app)}>
                    <input type="text" name="usernameInput" className="input" 
                    value={this.state.usernameInput} onChange={this.handleChange} 
                    placeholder="Username" />
                    <br/>

                    <input type="password" name="passwordInput" className="input" 
                    value={this.state.passwordInput} onChange={this.handleChange} 
                    placeholder="Password" />
                    <br/>

                    <input className="submitButton" type="submit" value="Log In"/>
                </form>

                <div className="backLogIn"> <SignUpBar handleClick={() => this.props.handleClick}/> </div>

            </div>
        );
    }
}

class SignUpBar extends React.Component{
    render(){
        return(
            <label> Don't have an account? 
                <button className="backLogInButtons" onClick={this.props.handleClick()}> 
                Sign Up! </button>
            </label>
        );
    }
}

class SignUpForm extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            usernameInput: "",
            passwordInput: "",
            confirmedpasswordInput:"",
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleChange(event){
        this.setState({[event.target.name]: event.target.value});
    }

    handleSubmit(event){
        alert("Account Created! but not really tho it's not implemented yet");
        //event.preventDefault();
        signupFetch(this.state, event.target)
        alert('after')
    }

    render(){
        return(
            <div>
                <form onSubmit={this.handleSubmit}>
                    <input type="text" name="usernameInput" className="input" 
                    value={this.state.usernameInput} onChange={this.handleChange} 
                    placeholder="Username" />
                    <br/>

                    <input type="password" name="passwordInput" className="input" 
                    value={this.state.passwordInput} onChange={this.handleChange} 
                    placeholder="Password" />
                    <br/>

                    <input type="password" name="confirmedpasswordInput" className="input" 
                    value={this.state.confirmedpasswordInput} onChange={this.handleChange} 
                    placeholder="Confirm Password" />
                    <br/>

                    <input className="submitButton" type="submit" value="Sign Up"/>
                </form>

                <div className="backLogIn"> <LogInBar handleClick={() => this.props.handleClick()}/> </div>
            </div>
        );
    }
}

class LogInBar extends React.Component{
    render(){
        return(
            <label> Already have an account? 
                <button className="backLogInButtons" onClick={this.props.handleClick}> 
                Log In </button>
            </label>
        );
    }
}

export default Login;
