import React from 'react';
import { Link } from 'react-router-dom';
import './CSS/Search.css'
import {uid} from 'react-uid';

class Search extends React.Component{
    render(){
        return(
            <div>
                <SearchBar/>
                <TableResults/>
            </div>
        )

    }
}

class TableResults extends React.Component{

    state = {
        lists:[
            {listName : "Beverages", 
            listDescript : "Some of my favorite homemade beverages",
            username : "John12"},

            {listName : "Dinner", 
            listDescript : "Food for dinner",
            username : "Nathan165"},

            {listName : "Candy", 
            listDescript : "For when I have a sweet tooth",
            username : "Juliet06e"},
        ]
    }


    Row(row){
        return(
            <tr key={uid(row)}>
                <td>
                    <Link to ={'./IndividualList'}>
                        <p>{row.listName}</p>
                    </Link>
                </td>
                <td>
                    <p>{row.listDescript}</p>
                </td>
                <td>
                    <Link to ={'./IndividualList'}>
                    <p>{row.username}</p>
                    </Link>
                </td>
            </tr>
        )
    }

    render(){
        return(
            <div id = "tableDiv">
            <table>
                <tbody>
                <tr>
                    <th>List Name</th>
                    <th>List Description</th>
                    <th>Username</th>
                </tr>
                {this.state.lists.map(row => {return this.Row(row)})}
                </tbody>
            </table>
            </div>
        )
    }
}


class SearchBar extends React.Component{
    state = {
        searchBy: 'food'
    }

    searchChanged = (event) => {
        const target = event.target
        const value = target.value
        const name = target.name

        this.setState({
            [name]: value
        })
    }

    render(){
        return(
            <div id="searchBarDiv">
            <form>
                <input type="text" id="searchBarText"></input>
                <input type="submit" id="searchBarButton" value ="Search"></input>
                <select name="searchBy" 
                        id="dropDownSearch"
                        onChange={this.searchChanged}>
                    <option value="list">List name</option>
                    <option value="food">Food name</option>
                </select>
            </form>
            </div>
        )
    }
}

class SearchInstructions extends React.Component{
    render(){
        return(
            <div>
                <p>In the drop down menu, select whether to search by a food name or a list.</p>
            </div>
        )
    }
}


export default Search;
