import React from 'react';
import { Link } from 'react-router-dom';
import './CSS/Search.css'
import {uid} from 'react-uid';
import { search_by_food } from './actions/search'

class Search extends React.Component{

    state = {
        list:[
            {listId: 1,
            listName : "Beverages", 
            listDescript : "Some of my favorite homemade beverages",
            username : "John12",
            userId: 2},

            {listId: 2,
            listName : "Dinner", 
            listDescript : "Food for dinner",
            username : "Nathan165",
            userId: 2},

            {listId: 3,
            listName : "Candy", 
            listDescript : "For when I have a sweet tooth",
            username : "Juliet06e",
            userId: 2},
        ]
    }

    render(){
        return(
            <div>
                <br></br>
                <strong>Search For Similar Lists!</strong>
                <SearchBar rows={this.state}/>
                <TableResults rows={this.state.list}/>
                <SearchInstructions rows={this.state.list}/>
            </div>
        )

    }
}

class TableResults extends React.Component{

    Row(row){
        return(
            <tr key={uid(row)}>
                <td>
                    <Link to ={'./IndividualList'}>
                        <p>{row.listName}</p>
                    </Link>
                </td>
                <td>
                    <p>{row.listDescript}</p>
                </td>
                <td>
                    <Link to ={'./Profile'}>
                    <p>{row.username}</p>
                    </Link>
                </td>
            </tr>
        )
    }

    render(){
        const { rows } = this.props
        return(
            <table id = "tableDiv">
                <tbody>
                <tr>
                    <th>List Name</th>
                    <th>List Description</th>
                    <th>Username</th>
                </tr>
                {rows.map(row => {return this.Row(row)})}
                </tbody>
            </table>
        )
    }
}


class SearchBar extends React.Component{
    state = {
        searchBy: 'food',
        searchInput: ""
    }

    searchChanged = (event) => {
        const target = event.target
        const value = target.value
        const name = target.name

        this.setState({
            [name]: value
        })
    }

    submitRequest = () =>{
        const { rows } = this.props
        /*
            Send GET request to server with the information in this.state.
            Will retrieve similar lists.
        */
       if(this.state.searchBy === 'food'){
           console.log("Inside")
          search_by_food(rows, this.state.searchInput)
       }

    }

    render(){
        return(
            <form id="searchBarForm">
                <select name="searchBy" 
                        id="dropDownSearch"
                        onChange={this.searchChanged}>
                    <option value="list">List name</option>
                    <option value="food">Food name</option>
                </select>
                <input type="text" id="searchBarText" name="searchInput" onChange={this.searchChanged}></input>
                <input type="button" id="searchBarButton" value ="Search" onClick={this.submitRequest}></input>           
            </form>

        )
    }
}

class SearchInstructions extends React.Component{
    render(){
        return(
            <div id="searchInstructions">
                <p>In the drop down menu, select whether to search by a food item or a list.</p>
                <p>If you search by a food item, you will be given lists that have that food item ranked at the top.</p>
                <p>If you search by a list name, lists that share as many of the same items will be found.</p>
            </div>
        )
    }
}


export default Search;
