import ENV from './../config.js'
const API_HOST = ENV.api_host

//send a friend request
export const send_friend_request = (username) => {
    const request = new Request(`${API_HOST}/add_potential_friend`, {
        method: "post",
        body: JSON.stringify({
            "username": username,
        }),
        headers: {
            Accept: "application/json, text/plain, */*",
            "Content-Type": "application/json"
        }
    });

    fetch(request)
        .then(res => {
            if (res.status === 200) {
                return res.json();
            }
        })
        .catch(error => {
            console.log(error);
        });
}

export const add_existing_friend =  (app,username) => {
    const request = new Request(`${API_HOST}/add_existing_friend`, {
        method: "post",
        body: JSON.stringify({
            "username": username,
        }),
        headers: {
            Accept: "application/json, text/plain, */*",
            "Content-Type": "application/json"
        }
    });

    fetch(request)
        .then(res => {
            if (res.status === 200) {
                return res.json()
            }
        })
        .then(json =>{
            app.setState({ExistingFriendsList : json.existing_friends})
            app.setState({PotentialFriendsList : json.potential_friends})
        })
        .catch(error => {
            console.log(error);
        });
}

